import matplotlib.pyplot as pltimport numpy as npTabA = np.loadtxt('/Users/alisha/Documents/LISA_ET/NonG/TabA.dat')TabB = np.loadtxt('/Users/alisha/Documents/LISA_ET/NonG/TabB.dat')TabC = np.loadtxt('/Users/alisha/Documents/LISA_ET/NonG/TabC.dat')TabD = np.loadtxt('/Users/alisha/Documents/LISA_ET/NonG/TabD.dat')#%%# Change the part in the " " to change the titles, filenames, and legends# For each of the response functions# Table namestaba = "TabA"tabb = "TabB"tabc = "TabC"tabd = "TabD"tabc1 = "TabC1"tabd1 = "TabD1"#For the combined graphstabcc1 = "TabC_C1_comb"tabdd1 = "TabD_D1_comb"#Graph locations# Just delete filepath if dont want it# IT MUST CONTAIN {nom} TO UPDATE THE FILENAME TO MATCH# EACH RESPONSE FUNCTIONsave = "/Users/alisha/Documents/LISA_ET/NonG/NonG{nom}.png"#%%plt.figure(figsize=(6, 9))plt.loglog(TabA[:,0], TabA[:,1], color = "indigo", linewidth=2.5)plt.grid(True)plt.title("{nom} Response Function".format(nom = taba), fontsize = 16)plt.xlabel('f (Hz)', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.savefig(save.format(nom = taba), bbox_inches='tight')#%%plt.figure(figsize=(6, 9))plt.loglog(TabB[:,0], TabB[:,1], color = "indigo", linewidth=2.5)plt.grid(True)plt.title("{nom} Response Function".format(nom = tabb), fontsize = 16)plt.xlabel('f (Hz)', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.savefig(save.format(nom = tabb), bbox_inches='tight')#%%plt.figure(figsize=(6, 9))plt.loglog(TabC[:,0], TabC[:,1], color = "indigo", linewidth=2.5)plt.grid(True)plt.title("{nom} Response Function".format(nom = tabc), fontsize = 16)plt.xlabel('f (Hz)', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.savefig(save.format(nom = tabc), bbox_inches='tight')#%%plt.figure(figsize=(6, 9))plt.loglog(TabD[:,0], TabD[:,1], color = "indigo", linewidth=2.5)plt.grid(True)plt.title("{nom} Response Function".format(nom = tabd), fontsize = 16)plt.xlabel('f (Hz)', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.savefig(save.format(nom = tabd), bbox_inches='tight')#%%# Better to use the function than the datafile heredef c1(x, x1s, sig):    res = (1 / 4**(1/sig)) * (1 / 5) * (1/2 + 1/2 * (x/x1s)**sig)**(-2/sig)    return resx = np.linspace(0.005,50,1000)y = c1(x, 1.1, 3)plt.figure(figsize=(6, 9))plt.loglog(x, y, color = "indigo", linewidth=2.5)plt.grid(True)plt.title("{nom} Response Function".format(nom = tabc1), fontsize = 16)plt.xlabel('f (Hz)', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.savefig(save.format(nom = tabc1), bbox_inches='tight')#%%# plt.figure(figsize=(6, 9))plt.loglog(TabC[:,0], TabC[:,1],'--', color = "red", linewidth=1.5, label=r"$\mathcal{R}^{(4)}_{A^\ell, E^\ell}$")plt.loglog(x, y, color = "red", linewidth=1.5, label=r"$\mathcal{R}^{(4)fit}_{A^\ell, E^\ell}$")plt.grid(True)plt.xlim(0.05,30)plt.legend(fontsize = 16, loc=3)# plt.title("{nom1} and {nom2} Response Function".format(nom1 = tabc, nom2 = tabc1), fontsize = 16)plt.xlabel(r'$f/f_\star$', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.tick_params(axis='both', which='major', labelsize=14) plt.savefig('/Users/alisha/Documents/LISA_ET/NonG/redrep2.png', bbox_inches='tight')plt.show()#%%#inD[x_, x1s_, \[Sigma]_, \[Gamma]_] :=  # 2^(-(8/\[Sigma]))    # 1.3  (3/100 (x/x1s)^(5) - \[Gamma] (x/x1s)^(4)) (1/2 +      # 1/2 (x/x1s)^(0.9 \[Sigma]))^(-8/\[Sigma]) def d1(x, x1s, sig, gam):    t1 = 2**(-(8/sig))*1.3*(3/100*(x/x1s)**5 - gam*(x/x1s)**4 )    t2 = (1/2 + 1/2*(x/x1s)**(0.9*sig))**(-(8/sig))    # res = 2**(-(8/sig)) * (3/100) * (x/x1s)**6 * (1/2 + 1/2 * (x/x1s)**sig)**(-8/sig)    return t1*t2m = np.linspace(0.05,100,1000)n = d1(m, 3.3, 8, 0.008)# plt.figure(figsize=(6, 9))plt.loglog(m, n, color = "indigo", linewidth=2.5)plt.grid(True)plt.title("{nom} Response Function".format(nom = tabd1), fontsize = 16)plt.xlabel('f (Hz)', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.savefig(save.format(nom = tabd1), bbox_inches='tight')#%%# plt.figure(figsize=(6, 9))plt.loglog(TabD[:,0], TabD[:,1],'--', color = "blue", linewidth=1.5, label=r"$\mathcal{R}^{(4)}_{T^\ell}$")plt.loglog(m, n, color = "blue", linewidth=1.5, label=r"$\mathcal{R}^{(4)fit}_{T^\ell}$")plt.grid(True)plt.ylim(1e-6,0.03)plt.xlim(0.05,30)plt.legend(fontsize = 16, loc=3)# plt.title("{nom1} and {nom2} Response Function".format(nom1 = tabd, nom2 = tabd1), fontsize = 16)plt.xlabel(r'$f/f_\star$', fontsize = 16)plt.ylabel(r'$\mathcal{R}$', fontsize = 20)plt.tick_params(axis='both', which='major', labelsize=14) plt.savefig('/Users/alisha/Documents/LISA_ET/NonG/bluerep2.png', bbox_inches='tight')plt.show()